import React, { useEffect, useState } from 'react';
import axios from 'axios';

function PopularBooks() {
    const [bookNames, setBookNames] = useState([]);
    const [authors, setAuthors] = useState([]);
    const [images, setImages] = useState([]);
    const [votes, setVotes] = useState([]);
    const [ratings, setRatings] = useState([]);

    useEffect(() => {
        axios.get('https://cjvajdcsse.execute-api.us-east-1.amazonaws.com/prod/getPopularBooks')
            .then((response) => {
                const data = JSON.parse(response.data.body);  // Parse the JSON string in `body`

                setBookNames(data.book_name || []);
                setAuthors(data.author || []);
                setImages(data.image || []);
                setVotes(data.votes || []);
                setRatings(data.rating || []);
            })
            .catch((error) => {
                console.error("Error fetching data:", error);
            });
    }, []);

    return (
        <div>
            <h2>Popular Books</h2>
            {bookNames.map((name, index) => (
                <div key={index}>
                    <h3>{name}</h3>
                    <p>Author: {authors[index]}</p>
                    <p>Votes: {votes[index]}</p>
                    <p>Rating: {ratings[index]}</p>
                    <img src={images[index]} alt={name} style={{ width: '100px', height: '150px' }} />
                </div>
            ))}
        </div>
    );
}

export default PopularBooks;
