import React, { useEffect, useState } from 'react';
import axios from 'axios';

function PopularBooks() {
    const [books, setBooks] = useState([]);

    useEffect(() => {
        axios.post(`${process.env.REACT_APP_API_URL}/getPopularBooks`, { operation: "getPopularBooks" })
            .then(response => {
                setBooks(response.data);
            })
            .catch(error => {
                console.error("Error fetching popular books:", error);
            });
    }, []);

    return (
        <div className="p-6">
            <h1 className="text-4xl font-bold mb-6">Top 50 Books</h1>
            <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-4">
                {books.map((book, index) => (
                    <div key={index} className="bg-gray-800 text-white p-4 rounded-lg shadow-lg">
                        <img className="w-full h-64 object-cover rounded-lg mb-4" src={book.image} alt={book.book_name} />
                        <h3 className="text-2xl font-bold">{book.book_name}</h3>
                        <p className="text-lg">{book.author}</p>
                        <p>Votes: {book.votes}</p>
                        <p>Rating: {book.rating}</p>
                    </div>
                ))}
            </div>
        </div>
    );
}

export default PopularBooks;
